import React from 'react';

const CharacterCard = ({ imageUrl, name, birthDate, era }) => {
  return (
    <div className="relative bg-transparent rounded-lg overflow-hidden shadow-lg hover:shadow-2xl transition duration-300">
      {/* Character Image */}
      <img 
        src={imageUrl} 
        alt={name} 
        className="w-full h-80 object-cover" 
      />
      {/* Character Info */}
      <div className="absolute bottom-0 bg-black bg-opacity-75 text-white w-full p-4 text-center">
        <h3 className="text-2xl font-bold">{name}</h3>
        <p className="text-sm">
          <strong>Born:</strong> {birthDate ? birthDate : 'Unknown'}
        </p>
        <p className="text-sm">
          <strong>Era:</strong> {era}
        </p>
      </div>
    </div>
  );
};

export default CharacterCard;
