import React, { useEffect, useState } from 'react';
import CharacterCard from '../components/CharacterCard'; // Import the card component

const CharacterPage = () => {
  const [characters, setCharacters] = useState([]);

  // Fetch characters from the backend
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-4xl font-bold text-center mb-8">Famous Historical Characters</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {characters.map((character) => (
          <CharacterCard key={character._id} character={character} /> // Use CharacterCard component here
        ))}
      </div>
    </div>
  );
};

export default CharacterPage;
