import React, { useEffect, useState } from 'react';

const AdminDashboard = () => {
  const [characters, setCharacters] = useState([]);

  // Fetch characters from the backend
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data); // Store the characters in state
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  // Handle character deletion
  const handleDelete = async (id) => {
    try {
      const response = await fetch(`http://localhost:5000/api/characters/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}` // Make sure you're passing token for authorization
        }
      });

      if (response.ok) {
        // Update character list after deletion
        setCharacters((prevCharacters) =>
          prevCharacters.filter((character) => character._id !== id)
        );
      } else {
        console.error('Failed to delete character');
      }
    } catch (error) {
      console.error('Error deleting character:', error);
    }
  };

  // Handle character update - For simplicity, this will just log the update action
  const handleUpdate = (id) => {
    // Navigate to an update page or show a modal to update character
    console.log(`Update character with id: ${id}`);
    // You would typically implement a form or redirect here
  };

  return (
    <div className="container mx-auto mt-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Admin Dashboard - Characters</h1>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white shadow-md rounded-lg">
          <thead>
            <tr>
              <th className="py-3 px-6 text-left">Name</th>
              <th className="py-3 px-6 text-left">Era</th>
              <th className="py-3 px-6 text-center">Actions</th>
            </tr>
          </thead>
          <tbody>
            {characters.length > 0 ? (
              characters.map((character) => (
                <tr key={character._id}>
                  <td className="py-3 px-6">{character.name}</td>
                  <td className="py-3 px-6">{character.era}</td>
                  <td className="py-3 px-6 flex justify-center space-x-4">
                    {/* Update Button */}
                    <button
                      onClick={() => handleUpdate(character._id)}
                      className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                    >
                      Update
                    </button>
                    {/* Delete Button */}
                    <button
                      onClick={() => handleDelete(character._id)}
                      className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" className="text-center py-4">
                  No characters found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminDashboard;
