import React, { useEffect, useState, useRef } from 'react';
import CharacterCard from '../components/CharacterCard';

const CharacterPage = () => {
  const [characters, setCharacters] = useState([]);
  const scrollRef = useRef(null);

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const scrollLeft = () => {
    scrollRef.current.scrollBy({ left: -300, behavior: 'smooth' });
  };

  const scrollRight = () => {
    scrollRef.current.scrollBy({ left: 300, behavior: 'smooth' });
  };

  return (
    <div className="container mx-auto py-8">
      <h1 className="text-3xl font-bold text-center mb-8">Characters</h1>
      <div className="relative">
        {/* Scrollable area */}
        <div 
          ref={scrollRef} 
          className="flex overflow-x-hidden space-x-6 justify-center"
        >
          {characters.map((character) => (
            <CharacterCard
              key={character._id}
              imageUrl={character.imageUrl}
              name={character.name}
            />
          ))}
        </div>
        {/* Scroll buttons */}
        <button 
          className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white px-3 py-2 rounded-full"
          onClick={scrollLeft}
        >
          &lt;
        </button>
        <button 
          className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white px-3 py-2 rounded-full"
          onClick={scrollRight}
        >
          &gt;
        </button>
      </div>
    </div>
  );
};

export default CharacterPage;
