import React, { useEffect, useState } from 'react';
import CharacterCard from './CharacterCard';
import { IconButton } from "@material-tailwind/react";  // Assuming you are using this library

const CharacterCarousel = () => {
  const [characters, setCharacters] = useState([]);
  const [position, setPosition] = useState(0); // Track the current position

  // Fetch characters from the server
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const moveLeft = () => {
    if (position > 0) {
      setPosition(position - 1);
    }
  };

  const moveRight = () => {
    if (position < characters.length - 7) { // Ensure we don't scroll past the last card
      setPosition(position + 1);
    }
  };

  return (
    <div className="relative w-full overflow-hidden">
      <h1 className="text-3xl font-bold text-center mb-8">Characters</h1>

      {/* Left Arrow Button */}
      <IconButton
        variant="text"
        color="white"
        size="lg"
        onClick={moveLeft}
        className="absolute top-1/2 left-4 -translate-y-1/2 z-10"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={2}
          stroke="currentColor"
          className="h-6 w-6"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M10.5 19.5L3 12m0 0l7.5-7.5M3 12h18"
          />
        </svg>
      </IconButton>

      {/* Carousel Cards */}
      <div
        className="flex transition-transform duration-500"
        style={{ transform: `translateX(-${position * 220}px)` }}  // Adjust for card width + margin space
      >
        {characters.map((character) => (
          <CharacterCard
            key={character._id}
            imageUrl={character.imageUrl}
            name={character.name}
          />
        ))}
      </div>

      {/* Right Arrow Button */}
      <IconButton
        variant="text"
        color="white"
        size="lg"
        onClick={moveRight}
        className="absolute top-1/2 right-4 -translate-y-1/2 z-10"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={2}
          stroke="currentColor"
          className="h-6 w-6"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M13.5 4.5L21 12m0 0l-7.5 7.5M21 12H3"
          />
        </svg>
      </IconButton>
    </div>
  );
};

export default CharacterCarousel;
