import React, { useEffect, useState } from 'react';
import BlogPost from '../components/BlogPost'; // Import the BlogPost component
import HomeNavbar from '../components/HomeNavbar'; // Import HomeNavbar

const BlogPage = () => {
  const [blogPosts, setBlogPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newPost, setNewPost] = useState({ title: '', content: '' });
  const [submitting, setSubmitting] = useState(false);

  // Fetch blog posts
  useEffect(() => {
    const fetchBlogPosts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/blogs');
        const data = await response.json();

        if (Array.isArray(data)) {
          setBlogPosts(data);
        } else {
          throw new Error("Unexpected data format. Expected an array.");
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching blog posts:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBlogPosts();
  }, []);

  // Handle input change for the new post form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPost((prevState) => ({ ...prevState, [name]: value }));
  };

  // Handle form submission
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:5000/api/blogs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newPost),
      });

      if (!response.ok) {
        throw new Error('Failed to create post');
      }

      const createdPost = await response.json();
      setBlogPosts((prevPosts) => [createdPost, ...prevPosts]); // Add new post to top of the list
      setNewPost({ title: '', content: '' }); // Reset form fields
      setSubmitting(false);
    } catch (err) {
      console.error('Error creating post:', err);
      setError(err.message);
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="text-center text-xl mt-20">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-xl mt-20 text-red-500">Error: {error}</div>;
  }

  return (
    <>
      <div className="absolute top-0 left-0 w-full z-10">
        <HomeNavbar />
      </div>
      <div className="container mx-auto py-8">
        <h1 className="text-3xl font-bold text-center mb-8">Blog</h1>

        {/* Form to Create New Post */}
        <div className="w-1/2 mx-auto mb-8"> {/* Set the width same as the blog posts */}
          <h2 className="text-2xl font-bold mb-4">Create a New Post</h2>
          <form onSubmit={handleFormSubmit} className="space-y-4">
            <input
              type="text"
              name="title"
              placeholder="Post title"
              value={newPost.title}
              onChange={handleInputChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg"
              required
            />
            <textarea
              name="content"
              placeholder="Post content"
              value={newPost.content}
              onChange={handleInputChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg"
              rows="6"
              required
            ></textarea>
            <div className="flex justify-end">
              <button
                type="submit"
                className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                disabled={submitting}
              >
                {submitting ? 'Submitting...' : 'Create Post'}
              </button>
            </div>
          </form>
        </div>

        {/* Blog Post List */}
        {blogPosts.length === 0 ? (
          <p className="text-center text-lg">No blog posts available.</p>
        ) : (
          <div className="space-y-8">
            {blogPosts.map((post) => (
              <BlogPost key={post._id} post={post} />
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default BlogPage;
