import React, { useEffect, useState, useRef } from 'react';
import CharacterCard from './CharacterCard';

const CharacterCarousel = () => {
  const [characters, setCharacters] = useState([]);
  const scrollRef = useRef(null);

  // Fetch characters from the server
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const scrollLeft = () => {
    scrollRef.current.scrollBy({ left: -350, behavior: 'smooth' });
  };

  const scrollRight = () => {
    scrollRef.current.scrollBy({ left: 350, behavior: 'smooth' });
  };

  return (
    <div className="relative w-full overflow-hidden h-screen flex items-center">
      <h1 className="text-3xl font-bold text-center mb-8">Characters</h1>
      
      {/* Left Arrow Button */}
      <button
        onClick={scrollLeft}
        className="absolute left-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-600 text-black rounded-full"
        style={{ zIndex: 1 }}
      >
        &lt;
      </button>

      {/* Carousel Cards */}
      <div 
        className="flex overflow-x-hidden space-x-6"
        ref={scrollRef}
      >
        {characters.map((character) => (
          <CharacterCard
            key={character._id}
            imageUrl={character.imageUrl}
            name={character.name}
          />
        ))}
      </div>

      {/* Right Arrow Button */}
      <button
        onClick={scrollRight}
        className="absolute right-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-600 text-white rounded-full"
        style={{ zIndex: 1 }}
      >
        &gt;
      </button>
    </div>
  );
};

export default CharacterCarousel;
