import React, { useEffect, useState } from 'react';
import CharacterCard from './CharacterCard';

const CharacterCarousel = () => {
  const [characters, setCharacters] = useState([]);
  const [position, setPosition] = useState(0); // Track the current position

  // Fetch characters from the server
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const moveLeft = () => {
    if (position > 0) {
      setPosition(position - 1);
    }
  };

  const moveRight = () => {
    if (position < characters.length - 7) { // Ensure we don't scroll past the last card
      setPosition(position + 1);
    }
  };

  return (
    <div className="relative w-full overflow-hidden">
    
      {/* Left Arrow Button */}
      <button
        onClick={moveLeft}
        className="absolute left-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-600 text-white rounded-full"
        style={{ zIndex: 1 }}
      >
        &lt;
      </button>

      {/* Carousel Cards */}
      <div
        className="flex transition-transform duration-500"
        style={{ transform: `translateX(-${position * 210}px)` }} // Adjust for card width + margin
      >
        {characters.map((character) => (
          <CharacterCard
            key={character._id}
            name={character.name} // Only passing the name prop
            imageUrl={character.imageUrl}
          />
        ))}
      </div>

      {/* Right Arrow Button */}
      <button
        onClick={moveRight}
        className="absolute right-0 top-1/2 transform -translate-y-1/2 p-2 bg-gray-600 text-white rounded-full"
        style={{ zIndex: 1 }}
      >
        &gt;
      </button>
    </div>
  );
};

export default CharacterCarousel;
