import React, { useEffect, useState, useRef } from 'react';
import CharacterCarousel from '../components/CharacterCarousel'; // Import the carousel component
import HomeNavbar from '../components/HomeNavbar';
import { useNavigate } from 'react-router-dom'; // Import the hook to navigate
import { FaSearch } from 'react-icons/fa'; // Import search icon
import backgroundImage from '../assets/character_background.png'; // Import the background image

const CharacterPage = () => {
  const [characters, setCharacters] = useState([]);
  const [searchQuery, setSearchQuery] = useState(''); // Store the search query
  const [filteredCharacters, setFilteredCharacters] = useState([]); // Store filtered characters based on search query
  const searchRef = useRef(null); // Use ref to detect clicks outside
  const navigate = useNavigate(); // To navigate to the character detail page

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  // Filter characters based on search query
  useEffect(() => {
    if (searchQuery.trim() === '') {
      setFilteredCharacters([]);
    } else {
      const filtered = characters
        .filter((character) =>
          character.name.toLowerCase().includes(searchQuery.toLowerCase())
        )
        .slice(0, 3); // Display up to 3 suggestions
      setFilteredCharacters(filtered);
    }
  }, [searchQuery, characters]);

  // Handle user selecting a character
  const handleCharacterSelect = (characterId) => {
    navigate(`/character/${characterId}`); // Redirect to the character detail page
  };

  // Handle click outside of search input to close suggestions
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setFilteredCharacters([]); // Close suggestions when clicking outside
      }
    };

    document.addEventListener('mousedown', handleClickOutside); // Add event listener for click
    return () => {
      document.removeEventListener('mousedown', handleClickOutside); // Clean up the event listener
    };
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center"
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundBlendMode: 'overlay',
        backgroundColor: 'rgba(0,0,0,0.6)',
      }}
    >
      <HomeNavbar />

      {/* Search Bar */}
      <div
        className="absolute top-20 right-10 w-full max-w-xs z-20" // Changed from max-w-md to max-w-sm for smaller width
        ref={searchRef}
      >
        <input
          type="text"
          className="w-full text-white bg-transparent border border-orange-300 px-4 py-2 rounded-lg pr-10" // Adjust padding for the icon
          placeholder="Search for a character..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)} // Update search query on input change
        />
        <FaSearch className="absolute right-4 top-1/2 transform -translate-y-1/2 text-orange-300" /> {/* Icon positioned inside input */}

        {filteredCharacters.length > 0 && (
          <ul className="absolute text-white bg-black bg-opacity-50 border border-orange-300 rounded-lg w-full mt-1 max-h-48 overflow-auto z-30"> {/* Ensure a higher z-index */}
            {filteredCharacters.map((character) => (
              <li
                key={character._id}
                className="p-2 cursor-pointer hover:bg-gray-100 hover:text-black"
                onClick={() => handleCharacterSelect(character._id)}
              >
                {character.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Character Carousel */}
      <div className="container mx-auto py-8 mt-12">
        <CharacterCarousel characters={characters} />
      </div>
    </div>
  );
};

export default CharacterPage;
