import React, { useState } from 'react';

const ChatBox = ({ characterId, characterName, characterImageUrl }) => {
  const [message, setMessage] = useState(''); // User's message input
  const [chatHistory, setChatHistory] = useState([]); // Store the chat messages
  const [loading, setLoading] = useState(false); // Track if the chatbot is responding

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message) return;

    const token = localStorage.getItem('token');
    console.log('Token:', token);  // Log token to verify

    try {
      setLoading(true); // Set loading state

      const response = await fetch(`http://localhost:5000/api/characters/${characterId}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,  // Attach the token here
        },
        body: JSON.stringify({ message }),
      });

      if (!response.ok) {
        throw new Error('Error interacting with character');
      }

      const data = await response.json();
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { role: 'user', content: message },
        { role: 'bot', content: data.response },
      ]);
      setMessage('');
    } catch (error) {
      console.error('Error interacting with character:', error);
    } finally {
      setLoading(false); // Set loading state to false after completion
    }
  };

  return (
    <div className="flex h-full">
      {/* Left side: Character image */}
      <div className="w-1/2 flex items-center justify-center p-4">
        <img
          src={characterImageUrl}
          alt={characterName}
          className="max-w-full max-h-full rounded-lg shadow-lg"
        />
      </div>

      {/* Right side: Chatbox */}
      <div className="w-1/2 p-6 flex flex-col">
        <div className="flex-grow border p-4 rounded-lg mb-4 h-64 overflow-y-auto bg-transparent">
          {chatHistory.length === 0 && (
            <p className="text-white">Start a conversation with {characterName}...</p>
          )}
          {chatHistory.map((chat, index) => (
            <div key={index} className={`mb-2 ${chat.role === 'user' ? 'text-right' : 'text-left'}`}>
              <span
                className={`inline-block px-4 py-2 rounded-lg ${
                  chat.role === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'
                }`}
              >
                {chat.content}
              </span>
            </div>
          ))}
        </div>

        {/* Input and button */}
        <form onSubmit={handleSubmit} className="flex">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Ask a question..."
            className="flex-grow px-4 py-2 border border-gray-300 rounded-l-lg focus:outline-none"
            disabled={loading} // Disable input while waiting for response
          />
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-r-lg disabled:bg-gray-400"
            disabled={loading} // Disable button while waiting for response
          >
            {loading ? 'Sending...' : 'Send'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatBox;
