import React from 'react';

const CharacterCard = ({ imageUrl, name }) => {
  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden w-[180px] h-[400px] flex-shrink-0 mx-2 hover:shadow-2xl transition duration-300">
      {/* Character Image */}
      <div className="relative">
        <img 
          src={imageUrl} 
          alt={name} 
          className="w-full h-[320px] object-cover" 
        />
        {/* Character Name Overlay */}
        <div className="absolute bottom-0 w-full text-white text-center py-2 bg-gradient-to-t from-black via-transparent to-transparent">
          <h3 className="text-xl font-bold">{name}</h3>
        </div>
      </div>
    </div>
  );
};

export default CharacterCard;
