import React from 'react';

const CharacterCard = ({ name, imageUrl }) => {
  return (
    <div className="relative w-[250px] h-[600px] flex-shrink-0 mx-4 overflow-hidden rounded-lg hover:shadow-2xl transition duration-300">
      {/* Character Image */}
      <img
        src={imageUrl}
        alt={name}
        className="object-cover w-full h-full"
      />
      {/* Character Name Overlay */}
      <div className="absolute bottom-0 w-full bg-gradient-to-t from-black to-transparent p-4">
        <h3 className="text-lg font-bold text-white text-center">{name}</h3>
      </div>
    </div>
  );
};

export default CharacterCard;
