import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, faComment } from '@fortawesome/free-solid-svg-icons'; // Import comment icon and heart icon
import Modal from '../components/Modal'; // Import the Modal component

const BlogPost = ({ post }) => {
  const [likeCount, setLikeCount] = useState(post.likes.length);
  const [isLiked, setIsLiked] = useState(false);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState(post.comments || []);
  const [showModal, setShowModal] = useState(false); // State for controlling Modal visibility

  useEffect(() => {
    const checkLiked = () => {
      const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage
      setIsLiked(post.likes.includes(userId));
    };
    checkLiked();
  }, [post.likes]);

  const handleLike = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/blogs/${post._id}/like`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      const updatedPost = await response.json();
      setLikeCount(updatedPost.likes.length);
      setIsLiked(!isLiked);
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/blogs/${post._id}/comment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ content: comment }),
      });
      const newComment = await response.json();
      setComments([...comments, newComment]);
      setComment('');
    } catch (error) {
      console.error('Error commenting on post:', error);
    }
  };

  return (
    <>
      <div className="py-6 border-b border-gray-300 mb-6 w-1/2 mx-auto"> {/* No border except under the post and width set to 50% */}
        {/* Post Header */}
        <div className="flex items-center mb-2">
          <div className="mr-3">
            <img
              src={post.author.profilePicture || 'https://via.placeholder.com/50'} // Replace with author profile picture
              alt={post.author.username}
              className="w-10 h-10 rounded-full"
            />
          </div>
          <div>
            <h3 className="text-lg font-bold">{post.author.username}</h3>
            <p className="text-xs text-gray-500">2 hr ago • Popular in your country</p>
          </div>
        </div>

        {/* Post Title and Content */}
        <h2 className="text-xl font-bold text-black mb-2">{post.title}</h2>
        <p className="text-gray-700 mb-4">{post.content}</p>

        {/* Like and Comment Buttons */}
        <div className="flex items-center space-x-4 mb-4">
          <button
            onClick={handleLike}
            className="flex items-center text-gray-500 hover:text-red-500 transition-colors"
          >
            <FontAwesomeIcon icon={faHeart} className={`mr-2 ${isLiked ? 'text-red-500' : ''}`} /> {/* Heart icon */}
            <span>{likeCount}</span>
          </button>

          <button
            className="flex items-center text-gray-600"
            onClick={() => setShowModal(true)} // Show the Modal when comment button is clicked
          >
            <FontAwesomeIcon icon={faComment} className="mr-2" /> {/* Comment icon */}
            {comments.length} Comments
          </button>
        </div>
      </div>

      {/* Modal to display comments */}
      {showModal && (
        <Modal show={showModal} onClose={() => setShowModal(false)}>
          <div className="py-6 border-b border-gray-300 mb-6 w-full"> {/* Full width in Modal */}
            {/* Reusing same structure inside modal */}
            <div className="flex items-center mb-2">
              <div className="mr-3">
                <img
                  src={post.author.profilePicture || 'https://via.placeholder.com/50'} // Replace with author profile picture
                  alt={post.author.username}
                  className="w-10 h-10 rounded-full"
                />
              </div>
              <div>
                <h3 className="text-lg font-bold">{post.author.username}</h3>
                <p className="text-xs text-gray-500">2 hr ago • Popular in your country</p>
              </div>
            </div>

            {/* Post Title and Content */}
            <h2 className="text-xl font-bold text-black mb-2">{post.title}</h2>
            <p className="text-gray-700 mb-4">{post.content}</p>

            {/* Like and Comment Buttons in Modal */}
            <div className="flex items-center space-x-4 mb-4">
              <button
                onClick={handleLike}
                className="flex items-center text-gray-500 hover:text-red-500 transition-colors"
              >
                <FontAwesomeIcon icon={faHeart} className={`mr-2 ${isLiked ? 'text-red-500' : ''}`} /> {/* Heart icon */}
                <span>{likeCount}</span>
              </button>

              <button className="flex items-center text-gray-600">
                <FontAwesomeIcon icon={faComment} className="mr-2" /> {/* Comment icon */}
                {comments.length} Comments
              </button>
            </div>
          </div>

          <div className="overflow-auto max-h-[50%]">
            <h3 className="text-lg font-bold mb-2">Comments</h3>
            {comments.length === 0 ? (
              <p>No comments yet.</p>
            ) : (
              <ul className="space-y-2">
                {comments.map((comment) => (
                  <li key={comment._id} className="p-2 bg-gray-100 rounded-lg">
                    <p>{comment.content}</p>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Add Comment Section */}
          <form onSubmit={handleCommentSubmit} className="mt-4">
            <input
              type="text"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add a comment"
              className="px-4 py-2 border border-gray-300 rounded w-full"
              required
            />
            <button type="submit" className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-full">
              Submit
            </button>
          </form>
        </Modal>
      )}
    </>
  );
};

export default BlogPost;
