import React, { useEffect, useState } from 'react';
import CharacterCarousel from '../components/CharacterCarousel';  // Import the carousel component
import HomeNavbar from '../components/HomeNavbar';
import backgroundImage from '../assets/background-image.jpg'; // Import the background image

const CharacterPage = () => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  return (
    <div
      className="min-h-screen bg-cover bg-center"
      style={{
        backgroundImage: `url(${backgroundImage})`, // Use the background image here
        backgroundSize: 'cover', // Ensures the image covers the entire area
        backgroundPosition: 'center', // Centers the background image
        backgroundRepeat: 'no-repeat', // Prevents repeating
      }}
    >
      <HomeNavbar />
      <div className="container mx-auto py-8">
        <h1 className="text-3xl font-bold text-center mb-8">Characters</h1>
        {/* Render CharacterCarousel here */}
        <CharacterCarousel characters={characters} />
      </div>
    </div>
  );
};

export default CharacterPage;
