import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Get the character ID from the URL

const CharacterDetailPage = () => {
  const { id } = useParams(); // Get character ID from the URL
  const [character, setCharacter] = useState(null);
  const [message, setMessage] = useState(''); // User's message input
  const [chatHistory, setChatHistory] = useState([]); // Store the chat messages
  const [loading, setLoading] = useState(false); // Track if the chatbot is responding

  useEffect(() => {
    const fetchCharacter = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/characters/${id}`);
        const data = await response.json();
        setCharacter(data);
      } catch (error) {
        console.error('Error fetching character:', error);
      }
    };

    fetchCharacter();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message) return;

    setLoading(true); // Start loading when message is sent

    const token = localStorage.getItem('authToken');

    try {
      const response = await fetch(`http://localhost:5000/api/characters/${id}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ message }),
      });
      const data = await response.json();

      // Append the user message and bot response to the chat history
      setChatHistory((prevHistory) => [
        ...prevHistory,
        { role: 'user', content: message }, // User's message
        { role: 'bot', content: data.response }, // Bot's response
      ]);
      
      setMessage(''); // Clear the input field after sending the message
      setLoading(false); // Stop loading when the response is received
    } catch (error) {
      console.error('Error interacting with character:', error);
      setLoading(false); // Stop loading if there's an error
    }
  };

  if (!character) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto py-8">
      <h1 className="text-4xl font-bold text-center mb-8">{character.name}</h1>
      <img src={character.imageUrl} alt={character.name} className="w-full h-auto mb-6" />
      <p className="text-lg mb-4"><strong>Description:</strong> {character.description}</p>
      <p className="text-lg mb-4"><strong>Birth Date:</strong> {character.birthDate}</p>
      <p className="text-lg mb-4"><strong>Death Date:</strong> {character.deathDate}</p>
      <p className="text-lg mb-4"><strong>Notable Works:</strong> {character.notableWorks}</p>
      <p className="text-lg"><strong>Era:</strong> {character.era}</p>

      {/* Chatbot Messaging Block */}
      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Chat with {character.name}</h2>
        <div className="border p-4 rounded-lg mb-4 h-64 overflow-y-auto bg-gray-100">
          {chatHistory.length === 0 && <p className="text-gray-500">Start a conversation with {character.name}...</p>}
          {chatHistory.map((chat, index) => (
            <div key={index} className={`mb-2 ${chat.role === 'user' ? 'text-right' : 'text-left'}`}>
              <span className={`inline-block px-4 py-2 rounded-lg ${chat.role === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'}`}>
                {chat.content}
              </span>
            </div>
          ))}
        </div>

        <form onSubmit={handleSubmit} className="flex">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Ask a question..."
            className="flex-grow px-4 py-2 border border-gray-300 rounded-l-lg focus:outline-none"
            disabled={loading} // Disable input while waiting for response
          />
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded-r-lg disabled:bg-gray-400"
            disabled={loading} // Disable button while waiting for response
          >
            {loading ? 'Sending...' : 'Send'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CharacterDetailPage;
