import React, { useEffect, useState, useRef } from 'react';
import CharacterCard from './CharacterCard';

const CharacterCarousel = () => {
  const [characters, setCharacters] = useState([]);
  const carouselRef = useRef(null); // Ref for the carousel

  // Fetch characters from the server
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  // Mouse and touch swipe handling
  const handleDragStart = (e) => {
    carouselRef.current.isDragging = true;
    carouselRef.current.startX = e.pageX || e.touches[0].pageX; // For both mouse and touch events
    carouselRef.current.scrollLeft = carouselRef.current.scrollLeft;
  };

  const handleDragMove = (e) => {
    if (!carouselRef.current.isDragging) return;

    const x = e.pageX || e.touches[0].pageX; // For both mouse and touch events
    const walk = (x - carouselRef.current.startX) * 1.5; // Scaled for effect
    carouselRef.current.scrollLeft = carouselRef.current.scrollLeft - walk;
  };

  const handleDragEnd = () => {
    carouselRef.current.isDragging = false;
  };

  const moveLeft = () => {
    carouselRef.current.scrollLeft -= 220; // Move left by the width of a card
  };

  const moveRight = () => {
    carouselRef.current.scrollLeft += 220; // Move right by the width of a card
  };

  return (
    <div className="relative w-full">
      <h1 className="text-3xl font-bold text-center mb-8">Characters</h1>
      
      {/* Left Arrow Button */}
      <button
        onClick={moveLeft}
        className="absolute left-2 top-1/2 transform -translate-y-1/2 p-4 bg-gray-600 text-white rounded-full z-50"
        style={{ zIndex: 10 }}  // Ensure button is above the carousel
      >
        &lt;
      </button>

      {/* Carousel Cards */}
      <div
        ref={carouselRef} // Attach ref to the carousel
        className="flex transition-transform duration-500 cursor-grab overflow-x-auto"
        onMouseDown={handleDragStart} // For mouse drag
        onMouseMove={handleDragMove}  // For mouse move
        onMouseUp={handleDragEnd}     // End dragging
        onMouseLeave={handleDragEnd}  // Stop dragging if the mouse leaves
        onTouchStart={handleDragStart} // For touch start
        onTouchMove={handleDragMove}   // For touch move
        onTouchEnd={handleDragEnd}     // End touch swipe
        style={{ userSelect: 'none', padding: '0 2rem' }} // Prevent text selection while dragging
      >
        {characters.map((character) => (
          <CharacterCard
            key={character._id}
            imageUrl={character.imageUrl}
            name={character.name}
          />
        ))}
      </div>

      {/* Right Arrow Button */}
      <button
        onClick={moveRight}
        className="absolute right-2 top-1/2 transform -translate-y-1/2 p-4 bg-gray-600 text-white rounded-full z-50"
        style={{ zIndex: 10 }}  // Ensure button is above the carousel
      >
        &gt;
      </button>
    </div>
  );
};

export default CharacterCarousel;
