import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo_navbar from '../assets/logo_navbar.png';

function HomeNavbar() {
  const navigate = useNavigate();

  // Handle the logout
  const handleLogout = () => {
    // Clear the JWT token from local storage
    localStorage.removeItem('token');
    // Redirect to login page
    navigate('/login');
  };

  return (
    <nav className="bg-black bg-opacity-30 w-full px-8 py-4 flex justify-between items-center backdrop-blur-sm">
      <div className="flex items-center text-2xl font-bold text-orange-300">
        <img 
          src={logo_navbar}
          alt="Echoes Of History Logo" 
          className="h-12 w-auto mr-2 object-contain"
          style={{ maxHeight: '50px' }}
        />
        <Link to="/" className="hover:text-orange-400">Echoes Of History</Link>
      </div>
      <div className="flex space-x-4">
        <Link to="/character">
          <button className="flex justify-center items-center min-w-[120px] px-4 py-2 border-2 border-orange-300 text-orange-300 font-semibold rounded-full hover:bg-orange-300 hover:text-black transition-colors duration-200">
            Characters
          </button>
        </Link>
        <Link to="/blog">
          <button className="flex justify-center items-center min-w-[120px] px-4 py-2 border-2  border-orange-300 text-orange-300 font-semibold rounded-full hover:bg-orange-300hover:text-black transition-colors duration-200">
            Blog
          </button>
        </Link>
        <button 
          onClick={handleLogout}  // Call the logout function on click
          className="flex justify-center items-center min-w-[120px] px-4 py-2 border-2 border-white text-white font-semibold rounded-full hover:bg-white hover:text-black transition-colors duration-200"
        >
          Logout
        </button>
      </div>
    </nav>
  );
}

export default HomeNavbar;
