import React, { useState } from 'react';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';

const CharacterCarousel = ({ characters }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const charactersPerPage = 3; // Number of characters visible at a time

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === characters.length - charactersPerPage ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? characters.length - charactersPerPage : prevIndex - 1
    );
  };

  return (
    <div className="relative w-full">
      {/* Left Arrow Button */}
      <button
        className="absolute left-0 top-1/2 transform -translate-y-1/2 z-10 bg-black bg-opacity-50 text-white p-3 rounded-full hover:bg-opacity-75 transition"
        onClick={prevSlide}
      >
        <FaChevronLeft size={24} />
      </button>

      {/* Right Arrow Button */}
      <button
        className="absolute right-0 top-1/2 transform -translate-y-1/2 z-10 bg-black bg-opacity-50 text-white p-3 rounded-full hover:bg-opacity-75 transition"
        onClick={nextSlide}
      >
        <FaChevronRight size={24} />
      </button>

      {/* Carousel Items */}
      <div className="flex space-x-4 overflow-hidden">
        {characters.slice(currentIndex, currentIndex + charactersPerPage).map((character) => (
          <div key={character._id} className="w-1/3 flex-shrink-0">
            <div className="relative w-full h-[80%] overflow-hidden rounded-lg shadow-lg">
              <img
                src={character.imageUrl}
                alt={character.name}
                className="object-cover w-full h-full"
              />
              <div className="absolute bottom-0 w-full bg-gradient-to-t from-black to-transparent p-4">
                <h3 className="text-lg font-bold text-white">{character.name}</h3>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CharacterCarousel;
