import React, { useState, useEffect } from 'react';

const BlogPost = ({ post }) => {
  const [likeCount, setLikeCount] = useState(post.likes.length);
  const [isLiked, setIsLiked] = useState(false);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState(post.comments || []);

  useEffect(() => {
    const checkLiked = () => {
      const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage
      setIsLiked(post.likes.includes(userId));
    };
    checkLiked();
  }, [post.likes]);

  // Like/unlike a blog post
  const handleLike = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/blog/${post._id}/like`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      const updatedPost = await response.json();
      setLikeCount(updatedPost.likes.length);
      setIsLiked(!isLiked);
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  // Comment on a blog post
  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/blog/${post._id}/comment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ content: comment }),
      });
      const newComment = await response.json();
      setComments([...comments, newComment]);
      setComment('');
    } catch (error) {
      console.error('Error commenting on post:', error);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md mb-6">
      <h2 className="text-2xl font-bold mb-2">{post.title}</h2>
      <p className="text-gray-700 mb-4">{post.content}</p>
      <div className="flex items-center space-x-4">
        <button
          onClick={handleLike}
          className={`px-4 py-2 rounded ${isLiked ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-700'}`}
        >
          {isLiked ? 'Unlike' : 'Like'} ({likeCount})
        </button>
      </div>
      <div className="mt-4">
        <h3 className="text-lg font-bold mb-2">Comments</h3>
        {comments.length === 0 ? (
          <p>No comments yet.</p>
        ) : (
          <ul className="space-y-2">
            {comments.map((comment) => (
              <li key={comment._id} className="p-2 bg-gray-100 rounded">
                <p>{comment.content}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
      <form onSubmit={handleCommentSubmit} className="mt-4">
        <input
          type="text"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Add a comment"
          className="px-4 py-2 border rounded w-full"
          required
        />
        <button type="submit" className="mt-2 px-4 py-2 bg-blue-500 text-white rounded">
          Submit
        </button>
      </form>
    </div>
  );
};

export default BlogPost;
