import React from 'react';

const CharacterCard = ({ imageUrl, name, birthDate, era }) => {
  return (
    <div className="bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-2xl transition duration-300">
      {/* Character Image */}
      <div className="relative w-full h-64">
        <img 
          src={imageUrl} 
          alt={name} 
          className="w-full h-full object-cover" 
        />
        <div className="absolute top-2 right-2">
          <button className="p-2 bg-red-600 text-white rounded-full">
            <i className="fas fa-play"></i>
          </button>
        </div>
      </div>
      
      {/* Character Info */}
      <div className="p-4">
        <h3 className="text-lg font-bold">{name}</h3>
        <p className="text-sm text-gray-600">
          <strong>Born:</strong> {birthDate ? birthDate : 'Unknown'}
        </p>
        <p className="text-sm text-gray-600">
          <strong>Era:</strong> {era}
        </p>
      </div>
    </div>
  );
};

export default CharacterCard;
