import React, { useEffect, useState } from 'react';
import BlogPost from '../components/BlogPost'; // Import the BlogPost component
import HomeNavbar from '../components/HomeNavbar'; // Import HomeNavbar

const BlogPage = () => {
  const [blogPosts, setBlogPosts] = useState([]);  // Ensure this is initialized as an empty array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);  // To handle potential errors
  const [newPost, setNewPost] = useState({ title: '', content: '' }); // State to hold new post data
  const [submitting, setSubmitting] = useState(false); // State to track form submission

  // Fetch existing blog posts
  useEffect(() => {
    const fetchBlogPosts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/blogs');
        const data = await response.json();

        if (Array.isArray(data)) {
          setBlogPosts(data);
        } else {
          throw new Error("Unexpected data format. Expected an array.");
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching blog posts:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBlogPosts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPost((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:5000/api/blogs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // Ensure the user is authenticated
        },
        body: JSON.stringify(newPost),
      });

      if (!response.ok) {
        throw new Error('Failed to create post');
      }

      const createdPost = await response.json();

      // Add the newly created post to the list of posts
      setBlogPosts((prevPosts) => [createdPost, ...prevPosts]);
      setNewPost({ title: '', content: '' }); // Reset the form fields
      setSubmitting(false);
    } catch (err) {
      console.error('Error creating post:', err);
      setError(err.message);
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="text-center text-xl mt-20">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-xl mt-20 text-red-500">Error: {error}</div>;
  }

  return (
    <>
      <HomeNavbar /> {/* Add the navbar */}
      <div className="container mx-auto py-8">
        <h1 className="text-3xl font-bold text-center mb-8">Blog</h1>

        {/* New Post Form */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-4">Create a New Post</h2>
          <form onSubmit={handleFormSubmit} className="space-y-4">
            <input
              type="text"
              name="title"
              placeholder="Post title"
              value={newPost.title}
              onChange={handleInputChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg"
              required
            />
            <textarea
              name="content"
              placeholder="Post content"
              value={newPost.content}
              onChange={handleInputChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg"
              rows="6"
              required
            ></textarea>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
              disabled={submitting}
            >
              {submitting ? 'Submitting...' : 'Create Post'}
            </button>
          </form>
        </div>

        {/* Display Existing Blog Posts */}
        {blogPosts.length === 0 ? (
          <p className="text-center text-lg">No blog posts available.</p>
        ) : (
          <div className="space-y-8">
            {blogPosts.map((post) => (
              <BlogPost key={post._id} post={post} />
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default BlogPage;
