import React, { useEffect, useState } from 'react';
import HomeNavbar from '../components/HomeNavbar';
import CharacterCard from '../components/CharacterCard'; // Import CharacterCard
import { FaArrowRight } from 'react-icons/fa'; // Import arrow icon
import BackgroundImage from '../assets/character_background.png';

function HomePage() {
  const [characters, setCharacters] = useState([]);
  const [featuredCharacters, setFeaturedCharacters] = useState([]);

  // Fetch characters from backend
  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters'); // Fetch all characters
        const data = await response.json();

        if (Array.isArray(data)) {
          setCharacters(data);

          // Randomly pick 3 characters as featured characters
          const randomCharacters = data.sort(() => 0.5 - Math.random()).slice(0, 3);
          setFeaturedCharacters(randomCharacters);
        }
      } catch (err) {
        console.error('Error fetching characters:', err);
      }
    };

    fetchCharacters();
  }, []);

  const navigateToCharactersPage = () => {
    window.location.href = '/characters'; // Navigate to the characters page
  };

  return (
    <div>
      <div className="absolute top-0 left-0 w-full z-10">
        <HomeNavbar />
      </div>

      <div
        className="relative h-screen bg-cover bg-center overflow-hidden"
        style={{
          backgroundImage: `url(${BackgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundBlendMode: 'overlay',
          backgroundColor: 'rgba(0,0,0,0.6)',
        }}
      >
        {/* Content Section */}
        <div className="container mx-auto py-8 h-full flex items-center">

          {/* Left Section: Featured Characters */}
          <div className="w-1/2 flex justify-start space-x-4">
            {featuredCharacters.map((character) => (
              <CharacterCard
                key={character._id}
                id={character._id}
                name={character.name}
                imageUrl={character.imageUrl}
              />
            ))}
          </div>

          {/* Right Section: Introduction and Explore More Button */}
          <div className="w-1/2 flex flex-col justify-center text-white">
            <div className="p-8 bg-opacity-80 bg-black rounded-lg shadow-lg">
              <h2 className="text-3xl font-bold mb-4">Talk with Famous Characters</h2>
              <p className="text-lg mb-8">
                Welcome to Echoes of History! Discover the stories and legacies of famous historical figures. Engage in conversations with characters from different eras and learn more about their lives, works, and the impact they left behind.
              </p>
              {/* Explore More Button */}
              <button
                className="flex items-center bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 transition duration-300"
                onClick={navigateToCharactersPage}
              >
                <span className="mr-2">Explore More</span>
                <FaArrowRight />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
