import React from 'react';
import { useNavigate } from 'react-router-dom';

const CharacterCard = ({ id, name, imageUrl }) => {
  const navigate = useNavigate();

  const handleCardClick = () => {
    navigate(`/character/${id}`); // Navigate to the character detail page
  };

  return (
    <div
      className="relative w-[230px] h-[700px] flex-shrink-0 mx-4 overflow-hidden rounded-lg hover:shadow-2xl transition duration-300 cursor-pointer"
      onClick={handleCardClick} // Trigger navigation on click
    >
      <img
        src={imageUrl}
        alt={name}
        className="object-cover w-full h-full"
      />
      {/* Text container with black transparent background */}
      <div className="absolute bottom-0 w-full bg-black bg-opacity-60 p-2 h-24">
        <h3 className="text-lg font-bold text-white text-center">{name}</h3>
      </div>
    </div>
  );
};

export default CharacterCard;
