import React, { useEffect, useState } from 'react';
import BlogPost from '../components/BlogPost'; // Import the BlogPost component
import HomeNavbar from '../components/HomeNavbar'; // Import HomeNavbar

// Import banner images from assets
import LeftBanner from '../assets/banner_left.png';
import RightBanner from '../assets/banner_right.png';
// Import background image from assets
import BackgroundImage from '../assets/character_background.png'; // Adjust the path as necessary

const BlogPage = () => {
  const [blogPosts, setBlogPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newPost, setNewPost] = useState({ title: '', content: '' });
  const [submitting, setSubmitting] = useState(false);

  // Fetch blog posts
  useEffect(() => {
    const fetchBlogPosts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/blogs');
        const data = await response.json();

        if (Array.isArray(data)) {
          setBlogPosts(data);
        } else {
          throw new Error("Unexpected data format. Expected an array.");
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching blog posts:', err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchBlogPosts();
  }, []);

  // Handle input change for the new post form
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPost((prevState) => ({ ...prevState, [name]: value }));
  };

  // Handle form submission
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:5000/api/blogs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newPost),
      });

      if (!response.ok) {
        throw new Error('Failed to create post');
      }

      const createdPost = await response.json();
      setBlogPosts((prevPosts) => [createdPost, ...prevPosts]); // Add new post to top of the list
      setNewPost({ title: '', content: '' }); // Reset form fields
      setSubmitting(false);
    } catch (err) {
      console.error('Error creating post:', err);
      setError(err.message);
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="text-center text-xl mt-20">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-xl mt-20 text-red-500">Error: {error}</div>;
  }

  return (
    <>
      <HomeNavbar />
      <div
        className="relative h-[calc(100vh-80px)] overflow-hidden bg-cover bg-center"
        style={{
          backgroundImage: `url(${BackgroundImage})`, // Setting the background image
        }}
      >
        {/* Left Banner */}
        <div className="absolute left-0 top-0 h-full lg:w-[400px]">
          <img
            src={LeftBanner} // Using imported banner image
            alt="Left Banner"
            className="w-full h-full object-cover"
          />
        </div>

        {/* Right Banner */}
        <div className="absolute right-0 top-0 h-full lg:w-[400px]">
          <img
            src={RightBanner} // Using imported banner image
            alt="Right Banner"
            className="w-full h-full object-cover"
          />
        </div>

        {/* Main Content (Centered) */}
        <div className="container mx-auto px-4 py-2 h-full flex flex-col justify-center bg-white bg-opacity-80" style={{ marginTop: '64px' }}> {/* Add top margin here */}

          {/* Form to Create New Post */}
          <div className="w-full mb-4 max-w-3xl mx-auto flex-grow"> {/* Reduced margin-bottom */}
            <h2 className="text-2xl font-bold mb-4 text-center">Create a New Post</h2>
            <form onSubmit={handleFormSubmit} className="space-y-4">
              <input
                type="text"
                name="title"
                placeholder="Post title"
                value={newPost.title}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                required
              />
              <textarea
                name="content"
                placeholder="Post content"
                value={newPost.content}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg"
                rows="4" /* Reduced rows */
                required
              ></textarea>
              <div className="flex justify-end">
                <button
                  type="submit"
                  className="px-6 py-2 bg-gray-500 text-white rounded-lg hover:bg-blue-600"
                  disabled={submitting}
                >
                  {submitting ? 'Submitting...' : 'Create Post'}
                </button>
              </div>
            </form>
          </div>

          {/* Blog Post List */}
          <div className="space-y-4 flex-grow overflow-auto"> {/* Set to flex-grow and overflow-auto to limit scrolling */}
            {blogPosts.length === 0 ? (
              <p className="text-center text-lg">No blog posts available.</p>
            ) : (
              blogPosts.map((post) => (
                <BlogPost key={post._id} post={post} />
              ))
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default BlogPage;
