import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, faComment } from '@fortawesome/free-solid-svg-icons'; // Import comment icon and heart icon
import Modal from '../components/Modal'; // Import the Modal component

// Function to format time difference
const timeAgo = (date) => {
  const now = new Date();
  const diffInSeconds = Math.floor((now - new Date(date)) / 1000);
  const diffInMinutes = Math.floor(diffInSeconds / 60);
  const diffInHours = Math.floor(diffInMinutes / 60);
  const diffInDays = Math.floor(diffInHours / 24);

  if (diffInMinutes < 1) {
    return `${diffInSeconds} second${diffInSeconds !== 1 ? 's' : ''} ago`;
  } else if (diffInMinutes < 60) {
    return `${diffInMinutes} minute${diffInMinutes !== 1 ? 's' : ''} ago`;
  } else if (diffInHours < 24) {
    return `${diffInHours} hour${diffInHours !== 1 ? 's' : ''} ago`;
  } else {
    return `${diffInDays} day${diffInDays !== 1 ? 's' : ''} ago`;
  }
};

const BlogPost = ({ post }) => {
  const [likeCount, setLikeCount] = useState(post.likes?.length || 0);  // Ensure likes is initialized
  const [isLiked, setIsLiked] = useState(false);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState(post.comments || []);
  const [showModal, setShowModal] = useState(false); // State for controlling Modal visibility

  // Set initial liked state from the server when the component mounts
  useEffect(() => {
    const userId = localStorage.getItem('userId'); // Assuming user ID is stored in localStorage

    // Check if post.likes is defined and is an array before using includes
    if (Array.isArray(post.likes) && post.likes.includes(userId)) {
      setIsLiked(true); // The user liked this post
    } else {
      setIsLiked(false);
    }
  }, [post.likes]);

  const handleLike = async () => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    try {
      // Toggle UI immediately (optimistic update)
      setLikeCount((prevCount) => (isLiked ? prevCount - 1 : prevCount + 1));

      // Send request to toggle like/unlike
      const response = await fetch(`http://localhost:5000/api/blogs/${post._id}/like`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });

      const updatedPost = await response.json();

      // Sync the like state with the server response
      setLikeCount(updatedPost.likes.length);
      setIsLiked(updatedPost.likes.includes(userId));

    } catch (error) {
      console.error('Error liking post:', error);

      // Revert UI in case of an error
      setIsLiked((prev) => !prev);
      setLikeCount((prevCount) => (isLiked ? prevCount + 1 : prevCount - 1));
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:5000/api/blogs/${post._id}/comment`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ content: comment }),
      });
      const newComment = await response.json();
      setComments([newComment, ...comments]); // Add new comment to the top
      setComment('');
    } catch (error) {
      console.error('Error commenting on post:', error);
    }
  };

  return (
    <>
      <div className="py-6 border-b border-gray-300 mb-6 w-1/2 mx-auto"> {/* No border except under the post and width set to 50% */}
        {/* Post Header */}
        <div className="flex items-center mb-2">
          <div className="mr-3">
            <img
              src={post.author.profilePicture || 'https://via.placeholder.com/50'} // Replace with author profile picture
              alt={post.author.username}
              className="w-10 h-10 rounded-full"
            />
          </div>
          <div>
            <h3 className="text-lg font-bold">{post.author.username}</h3>
            <p className="text-xs text-gray-500">{timeAgo(post.createdAt)} • Popular in your country</p>
          </div>
        </div>

        {/* Post Title and Content */}
        <h2 className="text-xl font-bold text-black mb-2">{post.title}</h2>
        <p className="text-gray-700 mb-4">{post.content}</p>

        {/* Like and Comment Buttons */}
        <div className="flex items-center space-x-4 mb-4">
          <button
            onClick={handleLike}
            className="flex items-center text-gray-500 hover:text-red-500 transition-colors"
          >
            <FontAwesomeIcon icon={faHeart} className={`mr-2 ${isLiked ? 'text-red-500' : ''}`} /> {/* Heart icon */}
            <span>{likeCount}</span>
          </button>

          <button
            className="flex items-center text-gray-600"
            onClick={() => setShowModal(true)} // Show the Modal when comment button is clicked
          >
            <FontAwesomeIcon icon={faComment} className="mr-2" /> {/* Comment icon */}
            {comments.length} Comments
          </button>
        </div>
      </div>

      {/* Modal to display comments */}
      {showModal && (
        <Modal show={showModal} onClose={() => setShowModal(false)}>
          <div className="py-6 border-b border-gray-300 mb-6 w-full"> {/* Full width in Modal */}
            {/* Reusing same structure inside modal */}
            <div className="flex items-center mb-2">
              <div className="mr-3">
                <img
                  src={post.author.profilePicture || 'https://via.placeholder.com/50'} // Replace with author profile picture
                  alt={post.author.username}
                  className="w-10 h-10 rounded-full"
                />
              </div>
              <div>
                <h3 className="text-lg font-bold">{post.author.username}</h3>
                <p className="text-xs text-gray-500">{timeAgo(post.createdAt)} • Popular in your country</p>
              </div>
            </div>

            {/* Post Title and Content */}
            <h2 className="text-xl font-bold text-black mb-2">{post.title}</h2>
            <p className="text-gray-700 mb-4">{post.content}</p>

            {/* Like and Comment Buttons in Modal */}
            <div className="flex items-center space-x-4 mb-4">
              <button
                onClick={handleLike}
                className="flex items-center text-gray-500 hover:text-red-500 transition-colors"
              >
                <FontAwesomeIcon icon={faHeart} className={`mr-2 ${isLiked ? 'text-red-500' : ''}`} /> {/* Heart icon */}
                <span>{likeCount}</span>
              </button>

              <button className="flex items-center text-gray-600">
                <FontAwesomeIcon icon={faComment} className="mr-2" /> {/* Comment icon */}
                {comments.length} Comments
              </button>
            </div>
          </div>

          {/* Comments Section */}
          <div className="relative max-h-[50%] overflow-auto">
            <div className="sticky top-0 bg-white z-10"> {/* Sticky "Comments" */}
              <h3 className="text-lg font-bold mb-2">Comments</h3>
            </div>
            {comments.length === 0 ? (
              <p>No comments yet.</p>
            ) : (
              <ul className="space-y-2">
                {comments
                  .slice()
                  .reverse() // Reverse the array to show the newest first
                  .map((comment) => (
                    <li key={comment._id} className="py-2 border-b border-gray-300 flex items-start space-x-4">
                      {/* Avatar */}
                      <img
                        src={comment.author?.profilePicture || 'https://via.placeholder.com/50'}
                        alt={comment.author?.username}
                        className="w-8 h-8 rounded-full"
                      />
                      <div>
                        {/* Username and comment content */}
                        <p className="font-bold">{comment.author?.username || 'Anonymous'}</p>
                        <p>{comment.content}</p>
                        {/* Time of comment */}
                        <p className="text-xs text-gray-500">{timeAgo(comment.createdAt)}</p>
                      </div>
                    </li>
                  ))}
              </ul>
            )}
          </div>

          {/* Add Comment Section */}
          <form onSubmit={handleCommentSubmit} className="mt-4 flex justify-between items-center absolute bottom-0 left-0 right-0 p-4 bg-white shadow-inner"> 
            <input
              type="text"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add a comment"
              className="flex-grow px-4 py-2 border border-gray-300 rounded-l-lg focus:outline-none"
              required
            />
            <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded-r-lg">
              Submit
            </button>
          </form>
        </Modal>
      )}
