import React, { useEffect, useState } from 'react';
import CharacterCard from '../components/CharacterCard';
import HomeNavbar from '../components/HomeNavbar'; // Assuming you have HomeNavbar

const CharacterPage = () => {
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  return (
    <>
      {/* Home Navbar */}
      <HomeNavbar />
      
      {/* Character Grid */}
      <div className="container mx-auto py-8">
        <h1 className="text-4xl font-bold text-center mb-8 text-gray-200">Famous Historical Figures</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-8 px-4">
          {characters.map((character) => (
            <CharacterCard
              key={character._id}
              imageUrl={character.imageUrl}
              name={character.name}
              birthDate={character.birthDate}
              era={character.era}
            />
          ))}
        </div>
      </div>
    </>
  );
};

export default CharacterPage;
