import React, { useEffect, useState, useRef } from 'react';
import CharacterCard from './CharacterCard';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa'; // Import arrow icons

const CharacterCarousel = () => {
  const [characters, setCharacters] = useState([]);
  const carouselRef = useRef(null);

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/characters');
        const data = await response.json();
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharacters();
  }, []);

  const scrollLeft = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: -300, behavior: 'smooth' });
    }
  };

  const scrollRight = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: 300, behavior: 'smooth' });
    }
  };

  return (
    <div className="relative w-full">
      {/* Left Button */}
      <button
        className="absolute left-0 top-1/2 transform -translate-y-1/2 z-10 text-white hover:scale-125 transition-transform duration-300"
        onClick={scrollLeft}
      >
        <FaChevronLeft size={30} />
      </button>

      {/* Carousel */}
      <div
        className="flex cursor-grab transition-transform duration-500 overflow-x-auto no-scrollbar"
        ref={carouselRef}
        style={{ userSelect: 'none' }}
      >
        {characters.map((character) => (
          <CharacterCard
            key={character._id}
            id={character._id} // Pass character ID as a prop
            imageUrl={character.imageUrl}
            name={character.name}
          />
        ))}
      </div>

      {/* Right Button */}
      <button
        className="absolute right-0 top-1/2 transform -translate-y-1/2 z-10 text-white hover:scale-125 transition-transform duration-300"
        onClick={scrollRight}
      >
        <FaChevronRight size={30} />
      </button>
    </div>
  );
};

export default CharacterCarousel;
